import 'antd/dist/antd.css';
import Curd from './Curd';
import { Input, DatePicker, Modal, Button } from 'antd';


const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "curd"
    }}>{`Curd`}</h2>
    <p>{`这一个相当重型的组件，使用它可以快速搭建一个包含增、删、改、查的模块。`}</p>
    <p>{`如果只需要一个表格，请使用 SearchTable`}</p>
    <h2 {...{
      "id": "curd-结构"
    }}>{`Curd 结构`}</h2>
    <ul>
      <li parentName="ul">{`表格`}</li>
      <li parentName="ul">{`表格搜索区`}</li>
      <li parentName="ul">{`表头操作区`}</li>
      <li parentName="ul">{`操作列`}</li>
    </ul>
    <p>{`如下图，其中红色区域是自带元素`}</p>
    <img src={require('./Curd.png')} style={{
      width: '100%'
    }} />
    <p>{`其中，每一结构包含如下默认功能`}</p>
