import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { Playground, Props } from 'docz';
import 'antd/dist/antd.css';
import FormUtil from './FormUtil';
import { IFormItemDataC } from '../interfaces/Demo';
import {Input, Form, Button, Checkbox} from 'antd';
import PhoneFormItem from '../forms/PhoneFormItem';


const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "formutil"
    }}>{`FormUtil`}</h1>
    <p>{`表单的辅助工具`}</p>
    <h2 {...{
      "id": "示例"
    }}>{`示例`}</h2>
    <h3 {...{
      "id": "渲染表单"
    }}>{`渲染表单`}</h3>
    <p>{`目标是解决表单对齐，间距的问题。
规则如下：`}</p>
    <ul>
      <li parentName="ul">{`默认一行2列，标签和内容的宽度为：1:4`}</li>
      <li parentName="ul">{`标签文字大于6个字，独占一行`}</li>
      <li parentName="ul">{`可手动修改：默认的列数、标签宽度`}</li>
      <li parentName="ul">{`可单独设置每一个表单项宽度、表单项标签的宽度`}</li>
    </ul>
    <h3 {...{
      "id": "一个标准示例"
    }}>{`一个标准示例`}</h3>
    <Playground __position={0} __code={'class Demo extends React.Component {\n    render() {\n      const list = [\n        {\n          span: 24,\n          content: (\n            <img\n              style={{\n                height: 160,\n                width: 120,\n                border: \'1px solid gray\',\n                display: \'inline-block\',\n              }}\n              alt=\"头像\"\n            />\n          ),\n        },\n        {\n          label: \'姓名\',\n          name: \'name\',\n          content: <Input />,\n          rules: [\n            {\n              required: true,\n            },\n          ],\n        },\n        {\n          label: \'启用\',\n          name: \'enable\',\n          valuePropName: \'checked\',\n          content: <Checkbox />,\n        },\n        {\n          label: \'性别\',\n          name: \'sex\',\n          content: <Input />,\n        },\n        {\n          label: \'公司地址\',\n          name: \'companyAddress\',\n          content: <Input />,\n        },\n        {\n          label: \'身份证号码\',\n          name: \'idCard\',\n          content: <Input />,\n        },\n        {\n          label: \'个人签名\',\n          span: 24,\n          name: \'sign\',\n          content: <Input />,\n        },\n        {\n          label: \'紧急联系人\',\n          span: 8,\n          name: \'friend\',\n          content: <Input />,\n        },\n        {\n          label: \'座机\',\n          span: 8,\n          name: \'phone\',\n          content: <Input />,\n        },\n        {\n          label: \'座机\',\n          span: 6,\n          name: \'phone2\',\n          content: <Input />,\n        },\n        {\n          label: \'座机\',\n          span: 6,\n          content: <Input />,\n        },\n        {\n          span: 24,\n          content: (\n            <Button type=\"primary\" htmlType=\"submit\">\n              保存，我是自动对齐\n            </Button>\n          ),\n        },\n      ]\n      return (\n        <div>\n          <Form\n            layout=\"Horizontal\"\n            onFinish={value => {\n              console.log(value)\n            }}\n          >\n            {FormUtil.renderFormItems(list)}\n          </Form>\n        </div>\n      )\n    }\n  }'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      FormUtil,
      IFormItemDataC,
      Input,
      Form,
      Button,
      Checkbox,
      PhoneFormItem
    }} __codesandbox={`undefined`} mdxType="Playground">
      {class Demo extends React.Component {
        render() {
          const list = [{
            span: 24,
            content: <img style={{
              height: 160,
              width: 120,
              border: '1px solid gray',
              display: 'inline-block'
            }} alt="头像" />
          }, {
            label: '姓名',
            name: 'name',
            content: <Input mdxType="Input" />,
            rules: [{
              required: true
            }]
          }, {
            label: '启用',
            name: 'enable',
            valuePropName: 'checked',
            content: <Checkbox mdxType="Checkbox" />
          }, {
            label: '性别',
            name: 'sex',
            content: <Input mdxType="Input" />
          }, {
            label: '公司地址',
            name: 'companyAddress',
            content: <Input mdxType="Input" />
          }, {
            label: '身份证号码',
            name: 'idCard',
            content: <Input mdxType="Input" />
          }, {
            label: '个人签名',
            span: 24,
            name: 'sign',
            content: <Input mdxType="Input" />
          }, {
            label: '紧急联系人',
            span: 8,
            name: 'friend',
            content: <Input mdxType="Input" />
          }, {
            label: '座机',
            span: 8,
            name: 'phone',
            content: <Input mdxType="Input" />
          }, {
            label: '座机',
            span: 6,
            name: 'phone2',
            content: <Input mdxType="Input" />
          }, {
            label: '座机',
            span: 6,
            content: <Input mdxType="Input" />
          }, {
            span: 24,
            content: <Button type="primary" htmlType="submit" mdxType="Button">保存，我是自动对齐</Button>
          }];
          return <div>
          <Form layout="Horizontal" onFinish={value => {
              console.log(value);
            }} mdxType="Form">
          {FormUtil.renderFormItems(list)}
          </Form>
        </div>;
        }

      }}
    </Playground>
    <h3 {...{
      "id": "使用特殊的formitem"
    }}>{`使用特殊的formItem`}</h3>
    <p>{`本示例，使用`}<inlineCode parentName="p">{`<PhoneFormItem />`}</inlineCode>{` 做为content值，此时需设置`}<inlineCode parentName="p">{`contentIsFormItem:true`}</inlineCode></p>
