      props: this ? this.props : props,
      Playground,
      Props,
      FormUtil,
      IFormItemDataC,
      Input,
      Form,
      Button,
      Checkbox,
      PhoneFormItem
    }} __codesandbox={`undefined`} mdxType="Playground">
      {class Demo extends React.Component {
        render() {
          const list = [{
            label: '手机号',
            span: 8,
            contentIsFormItem: true,
            content: <PhoneFormItem required name='mobilePhone' mdxType="PhoneFormItem" />
          }, {
            span: 24,
            content: <Button type="primary" htmlType="submit" mdxType="Button">保存</Button>
          }];
          return <div>
          <Form layout="Horizontal" onFinish={value => {
              console.log(value);
            }} mdxType="Form">
          {FormUtil.renderFormItems(list)}
          </Form>
        </div>;
        }

      }}
    </Playground>
    <h3 {...{
      "id": "一些特殊情况的处理"
    }}>{`一些特殊情况的处理`}</h3>
    <Playground __position={2} __code={'class Demo extends React.Component {\n    render() {\n      const list = [\n        {\n          content: (\n            <img\n              style={{\n                height: 160,\n                width: 120,\n                border: \'1px solid gray\',\n                display: \'inline-block\',\n              }}\n              alt=\"头像\"\n            />\n          ),\n        },\n        {\n          label: \'姓名\',\n          content: <Input />,\n        },\n        {\n          label: \'四个文字\',\n          content: <Input />,\n        },\n        {\n          label: \'这里六个文字\',\n          content: <Input />,\n        },\n        {\n          label:\n            \'我是标签字数超过7，会自动独占一行，但可能需要手动调整labe占位，否则显示不全\',\n          labelSpan: 15,\n          content: <Input value=\"我手动调整了标签占位\" />,\n        },\n        {\n          label: \'我也很长\',\n          span: 12,\n          content: <Input value=\"我也很长，但手动设置了只占半行\" />,\n        },\n        {\n          label: \'标签\',\n          span: 24,\n          content: <Input value=\"我标签文字不长，但手动设置了独占一行\" />,\n        },\n        {\n          content: <Button type=\"primary\">保存，我是自动对齐</Button>,\n        },\n      ]\n      return (\n        <div>\n          <Form layout=\"Horizontal\">{FormUtil.renderFormItems(list)}</Form>\n        </div>\n      )\n    }\n  }'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      FormUtil,
      IFormItemDataC,
      Input,
      Form,
      Button,
      Checkbox,
      PhoneFormItem
    }} __codesandbox={`undefined`} mdxType="Playground">
      {class Demo extends React.Component {
        render() {
          const list = [{
            content: <img style={{
              height: 160,
              width: 120,
              border: '1px solid gray',
              display: 'inline-block'
            }} alt="头像" />
          }, {
            label: '姓名',
            content: <Input mdxType="Input" />
          }, {
            label: '四个文字',
            content: <Input mdxType="Input" />
          }, {
            label: '这里六个文字',
            content: <Input mdxType="Input" />
          }, {
            label: '我是标签字数超过7，会自动独占一行，但可能需要手动调整labe占位，否则显示不全',
            labelSpan: 15,
            content: <Input value="我手动调整了标签占位" mdxType="Input" />
          }, {
            label: '我也很长',
            span: 12,
            content: <Input value="我也很长，但手动设置了只占半行" mdxType="Input" />
          }, {
            label: '标签',
            span: 24,
            content: <Input value="我标签文字不长，但手动设置了独占一行" mdxType="Input" />
          }, {
            content: <Button type="primary" mdxType="Button">保存，我是自动对齐</Button>
          }];
          return <div>
          <Form layout="Horizontal" mdxType="Form">
          {FormUtil.renderFormItems(list)}
          </Form>
        </div>;
        }

      }}
    </Playground>
    <h3 {...{
      "id": "渲染表单项--表单项为奇数时，按钮换行"
    }}>{`渲染表单项--表单项为奇数时，按钮换行`}</h3>
    <p>{`额外添加一个空项`}</p>
    <Playground __position={3} __code={'class Demo extends React.Component {\n    render() {\n      const list = [\n        {\n          label: \'姓名\',\n          content: <Input />,\n        },\n        {\n          label: \'性别\',\n          content: <Input />,\n        },\n        {\n          label: \'年龄\',\n          content: <Input />,\n        },\n        {\n          content: null,\n        },\n        {\n          content: <Button type=\"primary\">保存，我是自动对齐</Button>,\n        },\n      ]\n      return (\n        <div>\n          <Form layout=\"Horizontal\">{FormUtil.renderFormItems(list)}</Form>\n        </div>\n      )\n    }\n  }'} __scope={{
