    }} __codesandbox={`undefined`} mdxType="Playground">
      {class Demo extends React.Component {
        render() {
          const list = [{
            label: '姓名',
            content: <Input mdxType="Input" />
          }, {
            label: '性别',
            content: <Input mdxType="Input" />
          }, {
            label: '年龄',
            content: <Input mdxType="Input" />
          }, {
            content: null
          }, {
            content: <Button type="primary" mdxType="Button">保存，我是自动对齐</Button>
          }];
          return <div>
          <Form layout="Horizontal" mdxType="Form">
          {FormUtil.renderFormItems(list)}
          </Form>
        </div>;
        }

      }}
    </Playground>
    <h3 {...{
      "id": "渲染表单项--三列"
    }}>{`渲染表单项--三列`}</h3>
    <Playground __position={4} __code={'class Demo extends React.Component {\n    render() {\n      const list = [\n        {\n          label: \'姓名\',\n          content: <Input />,\n        },\n        {\n          label: \'性别\',\n          content: <Input />,\n        },\n        {\n          label: \'年龄\',\n          content: <Input />,\n        },\n        {\n          label: \'性别\',\n          content: <Input />,\n        },\n        {\n          content: <Button type=\"primary\">保存，我是自动对齐</Button>,\n        },\n      ]\n      return (\n        <div>\n          <Form layout=\"Horizontal\">{FormUtil.renderFormItems(list, 3)}</Form>\n        </div>\n      )\n    }\n  }'} __scope={{
