import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { Playground, Props } from 'docz';
import 'antd/dist/antd.css';
import { Form, InputNumber, Input, Radio, Modal, Button, message} from 'antd';
import EditForm from './EditForm';


const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "编辑表单"
    }}>{`编辑表单`}</h1>
    <p>{`通过配置表单项、新增、修改、获取详情的方法，即可得到一个编辑表单。`}</p>
    <p>{`编辑表单具有3种状态`}</p>
    <ul>
      <li parentName="ul">{`新增--source属性为空`}</li>
      <li parentName="ul">{`编辑--source属性不为空，updateFunction不为空`}</li>
      <li parentName="ul">{`阅读--source属性不为空，updateFunction为空`}</li>
    </ul>
    <p>{`编辑表单为你处理了`}</p>
    <ul>
      <li parentName="ul">{`根据不同状态显示或隐藏`}<inlineCode parentName="li">{`保存、取消`}</inlineCode>{`按钮`}</li>
      <li parentName="ul">{`请求时，显示保存按钮动画，禁用取消`}</li>
      <li parentName="ul">{`阅读状态下，给表单元素加上disbled属性`}</li>
      <li parentName="ul">{`借助FormUtil进行了元素对齐`}</li>
    </ul>
    <h2 {...{
      "id": "示例"
    }}>{`示例`}</h2>
    <h3 {...{
      "id": "新增"
    }}>{`新增`}</h3>
    <p><inlineCode parentName="p">{`source`}</inlineCode>{`为空时，为新增状态`}</p>
    <Playground __position={0} __code={'<EditForm\n  formItemList={[\n    {\n      name: \'nickName\',\n      label: \'姓名\',\n      content: <Input />,\n      rules: [\n        {\n          required: true,\n          message: \'请填写姓名\',\n        },\n      ],\n    },\n    {\n      name: \'age\',\n      label: \'年龄\',\n      content: <InputNumber />,\n    },\n  ]}\n  addFunction={value => {\n    Modal.confirm({\n      title: \'新增\',\n      content: JSON.stringify(value),\n    })\n    return fetch(\'./add\')\n  }}\n/>'} __scope={{
