      {class Demo extends React.Component {
        constructor(props) {
          super(props);
          this.formItemList = [{
            name: 'nickName',
            label: '姓名',
            content: <Input mdxType="Input" />,
            rules: [{
              required: true,
              message: '请填写姓名'
            }]
          }, {
            name: 'age',
            label: '年龄',
            content: <InputNumber mdxType="InputNumber" />
          }];
          this.state = {
            visibleModal: false,
            source: {
              id: 1,
              nickName: 'aaaa',
              age: 45
            },
            editSource: null,
            addFunction: null,
            updateFunction: null
          };
        }

        addFunction(value) {
          Modal.confirm({
            title: '新增',
            content: JSON.stringify(value)
          });
          return fetch('./add');
        }

        updateFunction(value) {
          Modal.confirm({
            title: '新增',
            content: JSON.stringify(value)
          });
          return fetch('./update');
        }

        closeEdit() {
          this.setState({
            visibleModal: false
          });
        }

        render() {
          const {
            visibleModal,
            editSource,
            updateFunction,
            addFunction,
            source
          } = this.state;
          return <div>
      <div>
        <Button style={{
                marginRight: 10
              }} onClick={() => {
                this.setState({
                  visibleModal: true,
                  addFunction: this.addFunction,
                  updateFunction: this.updateFunction,
                  editSource: null
                });
              }} mdxType="Button">
          新增
        </Button>
        <Button style={{
                marginRight: 10
              }} onClick={() => {
                this.setState({
                  visibleModal: true,
                  addFunction: this.addFunction,
                  updateFunction: this.updateFunction,
                  editSource: source
                });
              }} mdxType="Button">
          编辑
        </Button>
        <Button onClick={() => {
                this.setState({
                  visibleModal: true,
                  addFunction: this.addFunction,
                  updateFunction: null,
                  editSource: source
                });
              }} mdxType="Button">
          查看
        </Button>
      </div>
      <Modal visible={visibleModal} footer={null} title={editSource ? editSource.nickName : '新增'} onCancel={this.closeEdit.bind(this)} destroyOnClose mdxType="Modal">
        <EditForm onCancel={this.closeEdit.bind(this)} formItemList={this.formItemList} addFunction={addFunction} updateFunction={updateFunction} source={editSource} onOk={() => {
                this.closeEdit();
              }} mdxType="EditForm" />
      </Modal>
    </div>;
        }

      }}
    </Playground>
