      {class Demo extends React.Component {
        constructor(props) {
          super(props);
          this.formItemList = [{
            name: 'nickName',
            label: '姓名',
            content: <Input mdxType="Input" />,
            rules: [{
              required: true,
              message: '请填写姓名'
            }]
          }, {
            name: 'age',
            label: '年龄',
            content: <InputNumber mdxType="InputNumber" />
          }];
          this.state = {
            visibleModal: false,
            source: {
              id: 1,
              nickName: 'aaaa',
              age: 45
            },
            editSource: null,
            addFunction: null,
            updateFunction: null
          };
        }

        addFunction(value) {
          Modal.confirm({
            title: '新增',
            content: JSON.stringify(value)
          });
          return new Promise((resolve, reject) => {
            setTimeout(() => {
              fetch('./add');

              if (Math.random() > 0.5) {
                resolve();
              } else {
                reject('这里是服务器返回的错误原因');
              }
            }, 1000);
          });
        }

        closeEdit() {
          this.setState({
            visibleModal: false
          });
        }

        render() {
          const {
            visibleModal,
            addFunction
          } = this.state;
          return <div>
      <Button onClick={() => {
              this.setState({
                visibleModal: true,
                addFunction: this.addFunction
              });
            }} mdxType="Button">
        新增
      </Button>
      <Modal visible={visibleModal} footer={null} title='新增' onCancel={this.closeEdit.bind(this)} destroyOnClose mdxType="Modal">
        <EditForm onCancel={this.closeEdit.bind(this)} onOk={() => {
                message.success('保存成功');
                this.closeEdit();
              }} onError={error => {
                message.error('保存失败：' + error);
              }} formItemList={this.formItemList} addFunction={addFunction} mdxType="EditForm" />
      </Modal>
    </div>;
        }

      }}
    </Playground>
    <h2 {...{
      "id": "属性"
    }}>{`属性`}</h2>
    <Props of={EditForm} mdxType="Props" />
    </MDXLayout>;
}

