import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { Playground, Props } from 'docz';
import 'antd/dist/antd.css';
import SearchTable from './SearchTable';
import { Button, Form, Input } from 'antd';


const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "searchtable"
    }}>{`SearchTable`}</h1>
    <p>{`一个带搜索流程控制的表格组件`}</p>
    <h3 {...{
      "id": "主要做了以下工作"
    }}>{`主要做了以下工作`}</h3>
    <ul>
      <li parentName="ul">{`处理分页操作`}</li>
      <li parentName="ul">{`处理了请求中动画`}</li>
      <li parentName="ul">{`处理了选择操作`}</li>
      <li parentName="ul">{`请求参数变化时，自动发出请求`}</li>
      <li parentName="ul">{`数据不满一页时，自动隐藏分页器`}</li>
    </ul>
    <h3 {...{
      "id": "你可以"
    }}>{`你可以`}</h3>
    <ul>
      <li parentName="ul">{`全局设置pageSize，每个表格单独设置pageSize`}</li>
      <li parentName="ul">{`自定义一页的数量`}</li>
      <li parentName="ul">{`自定义是否使用“自动隐藏分页器”`}</li>
      <li parentName="ul">{`自定义请求方法`}</li>
      <li parentName="ul">{`自定义表格的附件元素，例如批量删除，搜索表单`}</li>
      <li parentName="ul">{`自定义搜索参数 `}</li>
      <li parentName="ul">{`自定义数据不满一页时，是否显示分页器`}</li>
      <li parentName="ul">{`自定义是否可选择`}</li>
      <li parentName="ul">{`自定义表格样式，类名`}</li>
      <li parentName="ul">{`透传props给antd的Table`}</li>
    </ul>
    <h2 {...{
      "id": "示例"
    }}>{`示例`}</h2>
    <p>{`涉及网络操作，建议在开发者工具中限制后观察示例`}</p>
    <h2 {...{
      "id": "基本用法"
    }}>{`基本用法`}</h2>
    <Playground __position={0} __code={'<SearchTable\n  columns={[\n    {\n      title: \'姓名\',\n      dataIndex: \'name\',\n    },\n  ]}\n  getListFunction={() => {\n    return fetch(\'./\').then(() => {\n      return {\n        total: 3,\n        dataSource: [\n          {\n            id: 1,\n            name: \'a\',\n          },\n          {\n            id: 2,\n            name: \'b\',\n          },\n          {\n            id: 3,\n            name: \'c\',\n          },\n        ],\n      }\n    })\n  }}\n/>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      SearchTable,
      Button,
      Form,
      Input
    }} __codesandbox={`undefined`} mdxType="Playground">
  <SearchTable columns={[{
        title: '姓名',
        dataIndex: 'name'
      }]} getListFunction={() => {
        return fetch('./').then(() => {
          return {
            total: 3,
            dataSource: [{
              id: 1,
              name: 'a'
            }, {
              id: 2,
              name: 'b'
            }, {
              id: 3,
              name: 'c'
            }]
          };
        });
      }} mdxType="SearchTable" />
    </Playground>
    <h2 {...{
      "id": "设置表可拖动"
    }}>{`设置表可拖动`}</h2>
    <Playground __position={1} __code={'<SearchTable\n  columns={[\n    {\n      title: \'姓名\',\n      dataIndex: \'name\',\n    },\n    {\n      title: \'年龄\',\n      dataIndex: \'age\',\n    },\n  ]}\n  dragEnable\n  getListFunction={() => {\n    return fetch(\'./\').then(() => {\n      return {\n        total: 1,\n        dataSource: [\n          {\n            id: 1,\n            name: \'a\',\n            age: 25,\n          },\n          {\n            id: 2,\n            name: \'b\',\n            age: 28,\n          },\n          {\n            id: 3,\n            name: \'c\',\n            age: 23,\n          },\n        ],\n      }\n    })\n  }}\n/>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      SearchTable,
      Button,
      Form,
      Input
    }} __codesandbox={`undefined`} mdxType="Playground">
  <SearchTable columns={[{
        title: '姓名',
        dataIndex: 'name'
      }, {
        title: '年龄',
        dataIndex: 'age'
      }]} dragEnable getListFunction={() => {
        return fetch('./').then(() => {
          return {
            total: 1,
            dataSource: [{
              id: 1,
              name: 'a',
              age: 25
            }, {
              id: 2,
              name: 'b',
              age: 28
            }, {
              id: 3,
              name: 'c',
              age: 23
            }]
          };
        });
      }} mdxType="SearchTable" />
    </Playground>
    <h2 {...{
      "id": "不自动隐藏分页器"
    }}>{`不自动隐藏分页器`}</h2>
    <Playground __position={2} __code={'<SearchTable\n  disableAutoHidePage\n  columns={[\n    {\n      title: \'姓名\',\n      dataIndex: \'name\',\n    },\n  ]}\n  getListFunction={() => {\n    return fetch(\'./\').then(() => {\n      return {\n        total: 3,\n        dataSource: [\n          {\n            id: 1,\n            name: \'a\',\n          },\n          {\n            id: 2,\n            name: \'b\',\n          },\n          {\n            id: 3,\n            name: \'c\',\n          },\n        ],\n      }\n    })\n  }}\n/>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      SearchTable,
      Button,
      Form,
      Input
    }} __codesandbox={`undefined`} mdxType="Playground">
  <SearchTable disableAutoHidePage columns={[{
        title: '姓名',
        dataIndex: 'name'
      }]} getListFunction={() => {
        return fetch('./').then(() => {
          return {
            total: 3,
            dataSource: [{
              id: 1,
              name: 'a'
            }, {
              id: 2,
              name: 'b'
            }, {
              id: 3,
              name: 'c'
            }]
          };
        });
      }} mdxType="SearchTable" />
    </Playground>
    <h2 {...{
      "id": "自定义pagesize"
    }}>{`自定义pageSize`}</h2>
    <Playground __position={3} __code={'<SearchTable\n  pageSize={2}\n  columns={[\n    {\n      title: \'姓名\',\n      dataIndex: \'name\',\n    },\n  ]}\n  getListFunction={(current, pageSize) => {\n    console.log(current, pageSize)\n    return fetch(\'./\').then(() => {\n      return {\n        total: 134,\n        dataSource: new Array(pageSize).fill(0).map((item, index) => {\n          const id = (current - 1) * pageSize + index\n          return {\n            id,\n            name: `name${id}`,\n          }\n        }),\n      }\n    })\n  }}\n/>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      SearchTable,
      Button,
      Form,
      Input
    }} __codesandbox={`undefined`} mdxType="Playground">
  <SearchTable pageSize={2} columns={[{
        title: '姓名',
        dataIndex: 'name'
      }]} getListFunction={(current, pageSize) => {
        console.log(current, pageSize);
        return fetch('./').then(() => {
          return {
            total: 134,
            dataSource: new Array(pageSize).fill(0).map((item, index) => {
              const id = (current - 1) * pageSize + index;
              return {
                id,
                name: `name${id}`
              };
            })
          };
        });
      }} mdxType="SearchTable" />
    </Playground>
    <h2 {...{
      "id": "添加附加操作"
    }}>{`添加附加操作`}</h2>
    <Playground __position={4} __code={'<SearchTable\n  pageSize={3}\n  selectedEnable\n  renderExtra={data => {\n    const { selectedRowKeys, selectedRows, loading, refresh } = data\n    return (\n      <div style={{ padding: \'10px 0\' }}>\n        <Button\n          disabled={!selectedRowKeys || !selectedRowKeys.length}\n          style={{ marginRight: 10 }}\n          loading={loading}\n          danger\n          onClick={() => {\n            alert(\n              selectedRowKeys ? selectedRowKeys.join() : \'请选择要删除的行\',\n            )\n          }}\n        >\n          删除全部\n        </Button>\n        <Button\n          loading={loading}\n          onClick={() => {\n            refresh()\n          }}\n        >\n          刷新\n        </Button>\n      </div>\n    )\n  }}\n  columns={[\n    {\n      title: \'姓名\',\n      dataIndex: \'name\',\n    },\n  ]}\n  getListFunction={(current, pageSize) => {\n    return fetch(\'./\').then(() => {\n      return {\n        total: 5,\n        dataSource: new Array(pageSize).fill(0).map((item, index) => {\n          const id = (current - 1) * pageSize + index\n          return {\n            id,\n            name: `name${id}`,\n          }\n        }),\n      }\n    })\n  }}\n/>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      SearchTable,
      Button,
      Form,
      Input
    }} __codesandbox={`undefined`} mdxType="Playground">
  <SearchTable pageSize={3} selectedEnable renderExtra={data => {
        const {
          selectedRowKeys,
          selectedRows,
          loading,
          refresh
        } = data;
        return <div style={{
          padding: '10px 0'
        }}>
          <Button disabled={!selectedRowKeys || !selectedRowKeys.length} style={{
            marginRight: 10
          }} loading={loading} danger onClick={() => {
            alert(selectedRowKeys ? selectedRowKeys.join() : '请选择要删除的行');
          }} mdxType="Button">
              删除全部
          </Button>
          <Button loading={loading} onClick={() => {
            refresh();
          }} mdxType="Button">
            刷新
          </Button>
        </div>;
      }} columns={[{
        title: '姓名',
        dataIndex: 'name'
      }]} getListFunction={(current, pageSize) => {
        return fetch('./').then(() => {
          return {
            total: 5,
            dataSource: new Array(pageSize).fill(0).map((item, index) => {
              const id = (current - 1) * pageSize + index;
              return {
                id,
                name: `name${id}`
              };
            })
          };
        });
      }} mdxType="SearchTable" />
    </Playground>
    <h2 {...{
      "id": "结合form设置请求参数"
    }}>{`结合form设置请求参数`}</h2>
    <p>{`在输入框输入文字，然后点击搜索按钮，观察请求参数`}</p>
    <Playground __position={5} __code={'class Demo extends React.Component {\n    constructor(props) {\n      super(props)\n      this.formInstance = null\n      this.state = {\n        formValue: null,\n        tableLoading: false,\n      }\n    }\n\n    renderExtra(props) {\n      const { loading, setSearchParams } = props\n      return (\n        <Form\n          ref={target => {\n            this.formInstance = target\n          }}\n          layout=\"inline\"\n          onFinish={value => {\n            setSearchParams(value)\n          }}\n          style={{ marginBottom: 10 }}\n        >\n          <Form.Item label=\"用户名\" name=\"userName\">\n            <Input />\n          </Form.Item>\n          <Button\n            type=\"primary\"\n            htmlType=\"submit\"\n            loading={loading}\n            style={{ marginRight: 10 }}\n          >\n            搜索\n          </Button>\n          <Button\n            loading={loading}\n            onClick={() => {\n              if (this.formInstance) {\n                this.formInstance.resetFields()\n                setSearchParams({})\n              }\n            }}\n          >\n            重置\n          </Button>\n        </Form>\n      )\n    }\n\n    render() {\n      const { tableLoading, formValue } = this.state\n      return (\n        <div>\n          <SearchTable\n            renderExtra={this.renderExtra}\n            columns={[\n              {\n                title: \'姓名\',\n                dataIndex: \'name\',\n              },\n            ]}\n            onLoadingChange={loading => {\n              this.setState({ tableLoading: loading })\n            }}\n            getListFunction={(current, pageSize, params) => {\n              return fetch(\n                `./?current=${current}&pageSize=${pageSize}&key=${\n                  params && params.userName ? params.userName : \'\'\n                }`,\n              ).then(() => {\n                return {\n                  total: 134,\n                  dataSource: new Array(pageSize)\n                    .fill(0)\n                    .map((item, index) => {\n                      const id = (current - 1) * pageSize + index\n                      return {\n                        id,\n                        name: `name${id}`,\n                      }\n                    }),\n                }\n              })\n            }}\n          />\n        </div>\n      )\n    }\n  }'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      SearchTable,
      Button,
      Form,
      Input
    }} __codesandbox={`undefined`} mdxType="Playground">
      {class Demo extends React.Component {
        constructor(props) {
          super(props);
          this.formInstance = null;
          this.state = {
            formValue: null,
            tableLoading: false
          };
        }

        renderExtra(props) {
          const {
            loading,
            setSearchParams
          } = props;
          return <Form ref={target => {
            this.formInstance = target;
          }} layout="inline" onFinish={value => {
            setSearchParams(value);
          }} style={{
            marginBottom: 10
          }} mdxType="Form">
      <Form.Item label="用户名" name="userName"><Input mdxType="Input" /></Form.Item>
      <Button type="primary" htmlType="submit" loading={loading} style={{
              marginRight: 10
            }} mdxType="Button">搜索</Button>
      <Button loading={loading} onClick={() => {
              if (this.formInstance) {
                this.formInstance.resetFields();
                setSearchParams({});
              }
            }} mdxType="Button">
        重置
      </Button>

    </Form>;
        }

        render() {
          const {
            tableLoading,
            formValue
          } = this.state;
          return <div>
      <SearchTable renderExtra={this.renderExtra} columns={[{
              title: '姓名',
              dataIndex: 'name'
            }]} onLoadingChange={loading => {
              this.setState({
                tableLoading: loading
              });
            }} getListFunction={(current, pageSize, params) => {
              return fetch(`./?current=${current}&pageSize=${pageSize}&key=${params && params.userName ? params.userName : ''}`).then(() => {
                return {
                  total: 134,
                  dataSource: new Array(pageSize).fill(0).map((item, index) => {
                    const id = (current - 1) * pageSize + index;
                    return {
                      id,
                      name: `name${id}`
                    };
                  })
                };
              });
            }} mdxType="SearchTable" />
    </div>;
        }

      }}
    </Playground>
    <h2 {...{
      "id": "属性"
    }}>{`属性`}</h2>
    <Props of={SearchTable} mdxType="Props" />
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
