      {class Demo extends React.Component {
        constructor(props) {
          super(props);
          this.state = {
            columns: []
          };
          this.formRef = React.createRef();
        }

        componentDidMount() {
          this.formRef.current.setFieldsValue({
            myFile: [{
              uid: '1',
              name: 'aaa.png',
              status: 'done',
              response: {
                id: 2
              },
              url: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png',
              thumbUrl: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png'
            }]
          });
        }

        render() {
          return <div>
      <Form ref={this.formRef} layout="inline" mdxType="Form">
        <Form.Item label="文件" name="myFile">
          <LimitUpload type={UploadType.IMAGE} action="https://jsonplaceholder.typicode.com/posts/" validateFile={file => {
                  console.log("file", file);

                  if (file.response && file.status === "done" && file.response.id) {
                    return true;
                  } else {
                    return false;
                  }
                }} mdxType="LimitUpload" />
        </Form.Item>
      </Form>
      <Button onClick={() => {
              this.formRef.current.setFieldsValue({
                myFile: [{
                  uid: '1',
                  name: 'bb.png',
                  status: 'done',
                  response: {
                    id: 1
                  },
                  url: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png',
                  thumbUrl: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png'
                }]
              });
            }} mdxType="Button">
        修改表单值
