import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { Playground, Props } from 'docz';
import 'antd/dist/antd.css';
import PowerImg from './PowerImg';
import { Select } from 'antd';
import  { Component, ReactNode } from 'react';
import ImageFitType from '../enums/ImageFitType';


const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "powerimg"
    }}>{`PowerImg`}</h1>
    <p>{`简单的图片封装，支持三种不拉伸的图片填充模式，图片预览`}</p>
    <h3 {...{
      "id": "主要做了以下工作"
    }}>{`主要做了以下工作`}</h3>
    <ul>
      <li parentName="ul">{`屏蔽拉伸类不友好的图片填充模式`}</li>
      <li parentName="ul">{`图片地址及宽高用属性设置`}</li>
      <li parentName="ul">{`配置预览功能`}</li>
    </ul>
    <h3 {...{
      "id": "后期"
    }}>{`后期`}</h3>
    <p>{`如果你需要更强大，定制的图片功能，请联系我`}</p>
    <h2 {...{
      "id": "示例"
    }}>{`示例`}</h2>
    <h3 {...{
      "id": "默认"
    }}>{`默认`}</h3>
    <Playground __position={0} __code={'class PowerImgDemo extends Component {\n    constructor(props) {\n      super(props)\n      this.state = {\n        width: \'120\',\n        height: \'80\',\n        objectFit: \'cover\',\n        isPreview: \'1\',\n      }\n      this.url =\n        \'http://my-bucket-1111-1301126387.cos-website.ap-guangzhou.myqcloud.com/media/large/2d13d416f80245b8cdacb342954ee5a2.jpg\'\n\n      this.widthPros = [\n        { key: \'1\', value: \'120\' },\n        { key: \'2\', value: \'480\' },\n        { key: \'3\', value: \'960\' },\n      ]\n\n      this.heightPros = [\n        { key: \'1\', value: \'80\' },\n        { key: \'2\', value: \'320\' },\n        { key: \'3\', value: \'560\' },\n      ]\n\n      this.objectFitPros = [\n        { key: \'1\', value: ImageFitType.COVER },\n        { key: \'2\', value: ImageFitType.CONTAIN },\n        { key: \'3\', value: ImageFitType.NONE },\n      ]\n\n      this.previewPros = [\n        { key: \'1\', value: \'1\', label: \'预览\' },\n        { key: \'2\', value: \'\', label: \'不预览\' },\n      ]\n    }\n\n    handleChange(value, prop) {\n      switch (prop) {\n        case \'width\':\n          this.setState({\n            width: value,\n          })\n          break\n        case \'height\':\n          this.setState({\n            height: value,\n          })\n          break\n        case \'objectFit\':\n          this.setState({\n            objectFit: value,\n          })\n          break\n        case \'isPreview\':\n          this.setState({\n            isPreview: value,\n          })\n          break\n        default:\n          break\n      }\n    }\n\n    render() {\n      const { width, height, objectFit, isPreview } = this.state\n      const style = {\n        backgroundColor: \'gray\',\n      }\n      return (\n        <div>\n          宽度设置\n          <Select\n            defaultValue=\"120\"\n            style={{ width: 120, marginRight: 15, marginLeft: 5 }}\n            onChange={value => this.handleChange(value, \'width\')}\n          >\n            {this.widthPros.map(item => (\n              <Select.Option key={item.key} value={item.value}>\n                {item.value}\n              </Select.Option>\n            ))}\n          </Select>\n          高度设置\n          <Select\n            defaultValue=\"80\"\n            style={{ width: 120, marginRight: 15, marginLeft: 5 }}\n            onChange={value => this.handleChange(value, \'height\')}\n          >\n            {this.heightPros.map(item => (\n              <Select.Option key={item.key} value={item.value}>\n                {item.value}\n              </Select.Option>\n            ))}\n          </Select>\n          填充模式\n          <Select\n            defaultValue=\"cover\"\n            style={{ width: 120, marginRight: 15, marginLeft: 5 }}\n            onChange={value => this.handleChange(value, \'objectFit\')}\n          >\n            {this.objectFitPros.map(item => (\n              <Select.Option key={item.key} value={item.value}>\n                {item.value}\n              </Select.Option>\n            ))}\n          </Select>\n          预览\n          <Select\n            defaultValue=\"1\"\n            style={{ width: 120, marginLeft: 5 }}\n            onChange={value => this.handleChange(value, \'isPreview\')}\n          >\n            {this.previewPros.map(item => (\n              <Select.Option key={item.key} value={item.value}>\n                {item.label}\n              </Select.Option>\n            ))}\n          </Select>\n          <div style={{ marginTop: 25 }}>\n            <PowerImg\n              style={style}\n              isPreview={!!isPreview}\n              objectFit={objectFit}\n              width={parseInt(width)}\n              height={parseInt(height)}\n              src={this.url}\n            />\n          </div>\n        </div>\n      )\n    }\n  }'} __scope={{
      props: this ? this.props : props,
